class Solution {

    // MARK: - Reverse

    func reverse(_ num: inout [Character], _ i: Int) {
        var i = i
        var j = num.count - 1

        while i < j {
            num.swapAt(i, j)
            i += 1
            j -= 1
        }
    }

    // MARK: - Next Permutation

    func nextPermutation(_ num: inout [Character]) {
        if num.count == 0 {
            return
        }

        var i = num.count - 1

        while i >= 1 {
            if num[i] > num[i - 1] {
                reverse(&num, i)
                for j in i..<num.count {
                    if num[j] > num[i - 1] {
                        num.swapAt(j, i - 1)
                        return
                    }
                }
            }
            i -= 1
        }

        for i in 1..<num.count {
            if num[num.count - i - 1] > num[num.count - i - 2] {
                reverse(&num, i)
                for j in (num.count - i - 1)..<num.count {
                    if num[j] > num[num.count - i - 2] {
                        num.swapAt(j, num.count - i - 2)
                        return
                    }
                }
            }
        }

        reverse(&num, 0)
    }

    // MARK: - Min Swaps

    func minSwaps(_ nums: inout [Character], _ target: [Character], _ size: Int) -> Int {
        var j = 0
        var swaps = 0

        for i in 0..<size {
            j = i
            while nums[j] != target[i] {
                j += 1
            }

            while i < j {
                nums.swapAt(j, j - 1)
                j -= 1
                swaps += 1
            }
        }

        return swaps
    }

    // MARK: - Get Min Swaps

    func getMinSwaps(_ num: String, _ k: Int) -> Int {

        var target = Array(num)

        for i in 0..<k {
            nextPermutation(&target)
        }

        var nums = Array(num)

        return minSwaps(&nums, target, num.count)
    }
}
