func getMinSwaps(num string, k int) int {
	afterSwap := []rune(num)
	for i := 0; i < k; i++ {
		nextBigger(afterSwap)
	}
	beforeSwap := []rune(num)
	swaps := 0
	for i := 0; i < len(afterSwap); i++ {
		if afterSwap[i] == beforeSwap[i] {
			continue
		}
		for j := i+1; j < len(afterSwap); j++ {
			if afterSwap[i] == beforeSwap[j] {
				// bubble j to i, need swap j-i times
				swaps += j-i
				copy(beforeSwap[i+1:], beforeSwap[i:j])
				break
			}
		}
	}
	return swaps
}

func nextBigger(num []rune) {
	maxValue := num[len(num)-1]
	counter := make([]rune, 10)
	counter[maxValue-'0']++
	for i := len(num)-2; i >= 0; i-- {
		counter[num[i]-'0']++
		if num[i] >= maxValue {
			// greater or equal, refresh maxValue
			maxValue = num[i]
			continue
		}
		// less than max value, move next bigger value to num[i]
		next := num[i]+1
		for counter[next-'0'] == 0 {
			next++
		}
		num[i] = next
		counter[next-'0']--
		// place other indexes
		for val, idx := '0', i+1; counter[val-'0'] != 0 || val < '9'; {
			if counter[val-'0'] != 0 {
				num[idx] = val
				counter[val-'0']--
				idx++
				continue
			}
			val++
		}
		break
	}
}
