	class Solution {
    public int getMinSwaps(String num, int k) 
    {
        StringBuilder sb = new StringBuilder(num);
        while(k-- > 0)
        {
            int index = findNextGreaterNumber(sb);
            swapAndSort(sb, index);
        }

        return swapCount(num, sb);
    }

    int swapCount(String num, StringBuilder sb)
    {
        int res = 0, i = 0, j = 0, size = num.length();
        while(i < size)
        {
            j = i;
            while(num.charAt(i) != sb.charAt(j))
            {
                j++;
            }

            while(i < j)
            {
                swap(sb, j, j-1);
                j--;
                res++;
            }

            i++;
        }

        return res;
    }

    void swap(StringBuilder sb, int i, int j)
    {
        char c = sb.charAt(i);
        sb.setCharAt(i, sb.charAt(j));
        sb.setCharAt(j, c);
    }

    int findNextGreaterNumber(StringBuilder sb)
    {
        int index = sb.length()-1;
        int minIndex = index;
        while(Character.getNumericValue(sb.charAt(index)) <= 
              Character.getNumericValue(sb.charAt(index-1)))
        {
            index--;
        }

        return index-1;
    }

    void swapAndSort(StringBuilder sb, int index)
    {
        char toSwap = sb.charAt(index);
        char justGreater = sb.charAt(index+1);
        int greater = index+1;
        for(int i = index+2; i < sb.length(); i++)
        {
            if(toSwap < sb.charAt(i) && sb.charAt(i) < justGreater)
            {
                justGreater = sb.charAt(i);
                greater = i;
