class Solution {
public:
    int getMinSwaps(string& num, int k) {
        string num_old = num;
        int N = (int)num.size();  
        for(int _ = 0; _ < k; ++_){
            bool dp[10] = {}; 
            int  id[10] = {};  
            int count[10] = {};

            int idx = N -1; 
            auto ptr = num.end() - 1; 
            bool found = false; 
            while(!found){   
                for(int i = (*ptr - '0') + 1; i < 10; ++i){
                    if(dp[i]){ 
                        swap(num[id[i]], num[idx]);
                        for(int j = idx + 1; j < N; ++j) ++count[num[j] - '0'];       
                        int j = idx + 1;
                        int idx2 = 0; 
                        while(idx2 < 10){
                            if(count[idx2]){
                                num[j++] = idx2 + 48;
                                --count[idx2];
                            } 
                            else ++idx2;  
                        }    
                        found = true;
                        break;
                    }
                } 
                dp[*ptr - '0'] = 1;
                id[*(ptr--) - '0'] = idx--;  
            }
        }   

        int cnt = 0, j;
        for(int i = 0; i < N; ++i){
            j = i; 
            while(num_old[i] != num[j])  ++j;     
            for(; j > i; --j, ++cnt) swap(num[j], num[j-1]);     

        }   
       return cnt;    
    }
};
