class Solution {
public:
	int getMinSwaps(std::string number, int k) {
		auto permuted = number;
		for (auto i = 0; i != k; ++i) {
			std::next_permutation(std::begin(permuted), std::end(permuted));
		}

		auto count = 0;
		while (number != permuted) {
			auto [iter, jter] = std::mismatch(std::begin(number), std::end(number), std::cbegin(permuted));
			auto value = *jter;
			auto kter = std::find(std::next(iter), std::end(number), value);
			std::rotate(iter, kter, std::next(kter));

			count += static_cast<int>(std::distance(iter, kter));
		}

		return count;
	}
};
