```
class Solution {
public:
    int getMinSwaps(string s, int k) {
        string temp = s;
        while(k--)
        {
           next_permutation(temp.begin() , temp.end()); 
        }
        int ans =0 ; 
        for(int i=0;i<s.size();i++)
        {
            if(s[i] != temp[i])
            {
                int j = i+1;
                while(j < s.size())
                {
                    if(s[j] == temp[i]) break;
                    j++;
                }
                s.insert(i, 1, temp[i]);

                s.erase(j+1,1);

                ans += j-i;

            }
        }
        return ans;
    }
