var getMinSwaps = function(num, k) {
    const digits = num.split('').map(ch => Number(ch))
    const len = digits.length

    function toNextPerm() {
        let i = -2 + len
        for (; i >= 0; i--)
            if (digits[i] < digits[1 + i])
                break

        let low = 1 + i, high = -1 + len
        const val = digits[i]
        while (low < high) {
            const mid = Math.ceil((low + high) / 2)
            if (digits[mid] > val)    low = mid
            else high = -1 + mid
        }

        const swapWith = low
        ;[digits[i], digits[swapWith]] = [digits[swapWith], digits[i]];

        for (let low = 1 + i, high = -1 + len;
        low < high; low++, high--) {
            [digits[low], digits[high]] = [digits[high], digits[low]]
        }
    }


    const snapshot = [...digits]
    for (let i = 0; i < k; i++)
        toNextPerm()


    let result = 0
    for (let i = 0; i < len; i++) {
        const valA = snapshot[i]
        const valB = digits[i]
        if (valA === valB)  continue

        // let snapshot[i] <- valB
        const index = snapshot.indexOf(valB, 1 + i)
        const offset = index - i
        result += offset

        // swap
        for (let j = index; j > i; j--) {
            [snapshot[j], snapshot[j - 1]] = [snapshot[j - 1], snapshot[j]]
        }
    }


    return result
};
