function countSwaps(n1, n2) {
  let count = 0;
  for (let i = 0; i < n1.length; i++) {
    if (n1[i] !== n2[i]) {
      count += findAndSwap(n2, n1[i], i, i+1);
    } else {
      continue;
    }
  }
  return count;
}

function findAndSwap(nums, val, swapIndex, start) {
  for (let i = start; i < nums.length; i++) {
    if (nums[i] === val) {
      return swapBetween(i, swapIndex, nums);
    }
  }
  throw new Error(`could not find elem to swap!`);
}

function swapBetween(end, start, nums) {
  let count = 0;
  for (let i = end; i > start; i--) {
    [nums[i], nums[i-1]] = [nums[i-1], nums[i]];
    count += 1;
  }

  return count;
}

function getNextPermutation(nums) {
  let count = 0;
  for (let i = nums.length-2; i >= 0; i--) {
    if (nums[i] < nums[i+1]) {
      for (let j = nums.length-1; j > i; j--) {
        if (nums[j] > nums[i]) {
          [nums[i], nums[j]] = [nums[j], nums[i]];
          reverse(i+1, nums.length-1, nums);
          return nums;
        }
      }
    }
  }

  throw new Error(`not a valid number: ${nums}`);
}

function reverse(start, end, nums) {
  while (start < end) {
    [nums[start], nums[end]] = [nums[end], nums[start]];
    start += 1;
    end -= 1;
  }
}
