class Solution {
    public int getMinSwaps(String num, int k) {
        int[] numsOriginal = new int[num.length()];
        int[] nums = new int[num.length()];
        int i = 0;
        for(char ch : num.toCharArray()){
            nums[i] = Character.getNumericValue(ch);
            numsOriginal[i] = nums[i];
            i++;
        }
        for(i =0 ; i < k; i++){
            nextPermutation(nums); 
        }
        return findDistance(nums, numsOriginal);
    }

    private int findDistance(int[] original, int[] nums){
        int ct = 0;
        int n = nums.length;
        for(int i = 0; i < n; i++){
            if(nums[i] == original[i]) continue;
            int fI = original[i];
            int k = i+1;
            for( ;k <n; k++){
                if(nums[k] == fI){
                    break;
                }
            }
            for(int j = k; j>i; j--){
                int tmp = nums[j];
                nums[j] = nums[j-1];
                nums[j-1] = tmp;
                ct++;
            }
        }

        return ct;
    }

    public void nextPermutation(int[] nums) {
        if(nums.length == 1) return;
