            int k = i+1;
            int index = -1;
            for(; k< nums.length; k++){
                if(nums[k] > nums[i]){
                    index = k;
                }
            }
            int temp = nums[i];
            nums[i] = nums[index];
            nums[index] = temp;
        }
        Arrays.sort(nums,i+1, nums.length);
    }
}

