public:
    int getMinSwaps(string num, int k) {
        int n = num.size();
        string s = num;
        while(k--) {
            next_permutation(s.begin(), s.end());
        }
        int pos = 0;
        while(pos < n && s[pos] == num[pos])
            pos++;
        int ans = 0;
        for(int i = pos; i < n; i++) {
            if(s[i] != num[i]) {
