    // O(n*k + n^2) O(n)
	public int getMinSwaps(String num, int k) {

		char[] nums = num.toCharArray();
		for (int i = 0; i < k; i++)
			nextPermutation(nums);

		return minKAdjacentSwaps(nums, num);
	}

	// O(n) O(1)
	public void nextPermutation(char[] nums) {

		int len = nums.length, ei = -1;
		for (int i = len - 1; i >= 1; i--) {
			int m = (nums[i - 1] - '0') - (nums[i] - '0');
			if (m < 0) {
				ei = i;
				break;
			}
		}

		if (ei == -1) {
			reverse(nums, 0, len - 1);
			return;
		}

		int idx = ei - 1, temp = nums[idx] - '0';
		ei = binarySearch(nums, ei, len - 1, temp);

		swap(nums, idx, ei);
		reverse(nums, idx + 1, len - 1);

	}

	// O(logn) O(1)
	public int binarySearch(char[] nums, int hi, int lo, int target) {

		int ans = -1;
		while (lo >= hi) {

			int mid = (lo + hi) / 2;
			if (nums[mid] - '0' > target) {
				ans = mid;
				hi = mid + 1;
			} else
				lo = mid - 1;
		}

		return ans;
	}

	// O(n) O(1)
	public void reverse(char[] nums, int si, int ei) {

		while (si < ei) {
			swap(nums, si, ei);
			si++;
			ei--;
		}

	}

	// O(1) O(1)
	public void swap(char[] nums, int i, int j) {

		char temp = nums[i];
		nums[i] = nums[j];
		nums[j] = temp;
	}

	// O(n^2) O(1)
	public int minKAdjacentSwaps(char[] src, String target) {

		int len = src.length, i = 0, j = 0, count = 0;
		while (i < len) {

			if (src[i] != target.charAt(j)) {
				for (int k = i + 1; k < len; k++) {
					if (src[k] == target.charAt(j)) {
						for (int l = k; l >= i + 1; l--) {
							swap(src, l, l - 1);
							count++;
						}
						break;
					}
				}
			}
			i++;
			j++;
		}

		return count;
	}

