class Solution {
    func getMinSwaps(_ num: String, _ k: Int) -> Int {
        let numOriginal = num.map({ Int($0.asciiValue ?? 48) - 48 }), n = numOriginal.count
        var num = numOriginal, result = 0
        for i in 0..<k {
            nextPermutation(&num)
        }
        main: while true {
            for i in 0..<n where numOriginal[i] != num[i] {
                var j = i
                while num[j] != numOriginal[i] { j += 1 }
                num.remove(at: j)
                num.insert(numOriginal[i], at: i)
                result += j - i
                continue main
            }
            break
        }
        return result
    }
    func nextPermutation(_ nums: inout [Int]) { // https://leetcode.com/problems/next-permutation/
        let c = nums.count
        var i: Int = c - 1, j: Int = 0
        while i > 0 { // try to find ascending sequence
            if nums[i] > nums[i - 1] {
                break
            }
            i -= 1
        }
        if i > 0 { // ascending sequence exists, let's find next greater number
            j = i
            while j < c {
                if nums[i - 1] >= nums[j] {
                    break
                }
                j += 1
            }
            nums.swapAt(i - 1, j - 1) // swap
        }
        j = i
        while j <= (i + (c - i) / 2 - 1) && nums[j] > nums[c - 1 - (j - i)] { // final sort (swap desc to asc)
            nums.swapAt(j, c - 1 - (j - i))
            j += 1
        }
    }
}
