class Solution:
    def minInterval(self, intervals: List[List[int]], queries: List[int]) -> List[int]:
        q=sorted(set(queries))
        n=len(q)

        intervals.sort(key=lambda x:x[1]-x[0])

        ans=[-1]*n
        par=list(range(n+1))    
        def find(x):
            if par[x]!=x:
                par[x]=find(par[x])
            return par[x]

        for a,b in intervals:
            l,r=bisect_left(q,a),bisect_right(q,b)
            v=find(l)
            while v<r:
                ans[v]=b-a+1
                par[v]=v+1
                v=find(v)

        d={n:i for i,n in enumerate(q)}
        return [ans[d[qi]] for qi in queries]
