class Solution {

    // define the segment tree structure
    class SegmentTreeNode {
        int start;
        int end;
        int min;
        SegmentTreeNode left;
        SegmentTreeNode right;
        public SegmentTreeNode(int start, int end){
            this.start = start;
            this.end = end;
            this.min = Integer.MAX_VALUE;
        }
    }

    private SegmentTreeNode root = null;

    public int[] minInterval(int[][] intervals, int[] queries) {
        // initial segement root
        root = new SegmentTreeNode(1, (int)(1e7+1));

        // insert interval into segment tree
        for(int[] interval : intervals){
            int size = interval[1] - interval[0] + 1;
            update(root, interval[0], interval[1], size);
        }

        // query the result
        int[] result = new int[queries.length];
        for(int i=0; i<queries.length; i++){
            result[i] = getMinSize(queries[i]);
        }
        return result;
    }

    private void update(SegmentTreeNode root, int left, int right, int val) {
        if (left > root.end || right < root.start) {
            return;
        }
        // if current segment is between [left, right], update the min interval
        if (left <= root.start && root.end <= right) {
            root.min = Math.min(root.min, val);
            return;
        }

        // if [left, right] is covered in both side segemets, update the both children segments
        int mid = root.start + (root.end - root.start) / 2;
        // add the left child segment node if required
        if(root.left == null){
            root.left = new SegmentTreeNode(root.start, mid);
        }
        update(root.left, left, right, val);
        // add the right child segment node if required
        if(root.right == null){
           root.right = new SegmentTreeNode(mid+1, root.end); 
        }
        update(root.right, left, right, val);
    }

    private int getMinSize(int query) {
        int result = getMinSize(root, query);
        return result == Integer.MAX_VALUE ? -1 : result;
    }

	// find all the segment node which include query, return the min interval of them
    private int getMinSize(SegmentTreeNode root, int query) {
        if(root == null) {
            return Integer.MAX_VALUE; 
        }
        if(query > root.end || query < root.start) {
            return Integer.MAX_VALUE;
        } 
        if (root.start == root.end) {
            return root.min;
        } else {
            // since query is a single point, so go to left/right segment based on its index
            int mid = root.start + (root.end - root.start) / 2;
            if(query <= mid) {
                return Math.min(root.min, getMinSize(root.left, query));
            } else {
                return Math.min(root.min, getMinSize(root.right, query));
            }
        }
    }
}
