    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {
        int z=0;
        for(auto &x:intervals){
            z=max(z,x[1]);
        }
        vector<vector<int>> diff(z+5);
        for(auto &x:intervals){ //O(|INTERVALS|)
            diff[x[0]].push_back(x[1]-x[0]+1);
            diff[x[1]+1].push_back(-1*(x[1]-x[0]+1));
        }
        vector<int> ans(z+1);
        multiset<int> s;
        for(int i=0;i<=z;i++){ //O(1E7+LOGN)
            for(auto &x:diff[i]) {
                if(x>0)
                    s.insert(x);
                else
                    s.erase(s.find(-1*x));
            }
            ans[i]=*s.begin();
        }
        vector<int> a;
        for(auto &x:queries){ //O(|QUERIES|)
            if(ans[x]==0) ans[x]=-1;
            a.push_back(ans[x]);
        }
        return a;
    }
};
