		sort(intervals.begin(), intervals.end(), [](const vector<int> &a, const vector<int> &b){
			return a[1] - a[0] < b[1] - b[0];
		});

		int n = queries.size();
		set<pair<int, int>> st;
		for(auto i = 0; i < n; i++){
			st.insert({queries[i], i});
		}
		vector<int> res(n, -1);

		for(auto &it : intervals){
