class Solution {
    public int[] minInterval(int[][] intervals, int[] queries) {
        int qlen=queries.length;
        int tempquery[]=new int[qlen];
        for (int  i=0;i<qlen;i++)
        {
            tempquery[i]=queries[i];
        }
        HashMap<Integer,Integer>hashMap=new HashMap<>();
        for(int i=0;i<qlen;i++)
        {
            hashMap.put(queries[i],i);
        }
        Arrays.sort(queries);
        Arrays.sort(intervals,(a,b)->a[0]-b[0]);
        PriorityQueue<int[]>priorityQueue=new PriorityQueue<>(Comparator.comparingInt(a -> (a[1] - a[0])));
        int ind=0;
        int intind=0;
        int find=0;

        int answer[]=new int[qlen];
        while (ind<qlen)
        {
            while (intind<intervals.length)
            {
                if (intervals[intind][0]<=queries[ind]&&intervals[intind][1]>=queries[ind])
                {
                    priorityQueue.add(intervals[intind]);
                    intind++;
                }
                else if (intervals[intind][0]>queries[ind])
                {
                    break;
                }
                else
                {
                    intind++;
                }
            }
            while (!priorityQueue.isEmpty()&& priorityQueue.peek()[1]<queries[ind])
            {
                priorityQueue.poll();
            }
            if (priorityQueue.isEmpty())
            {
                answer[hashMap.get(queries[ind])]=-1;
