```
class Solution {
public:
    class node {
        public:
        int start, end, mid, interval;
        node *left, *right;
        node(int x, int y){
            start=x;
            end=y;
            mid=(x+y)/2;
            interval=INT_MAX;
            left=NULL;
            right=NULL;
        }
    };

    void add(int start, int end, int range, node *root){
        if(end<root->start || start>root->end || root->interval<=range)
            return;
        if(start<=root->start && end>=root->end)
            root->interval = min(range, root->interval);
        else
        {
            if(!root->left)
                root->left=new node(root->start, root->mid);
            add(start, end, range, root->left);
            if(!root->right)
                root->right=new node(root->mid+1, root->end);
            add(start, end, range, root->right);
        }
    }

    int MinRange(int num, node *root)
    {
        if(!root || num<root->start || num>root->end)
            return INT_MAX;
        return min(root->interval, min(MinRange(num, root->left), MinRange(num, root->right)));
    }

    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {
        node *root=new node(1, 10000000);
        for(auto i: intervals)
            add(i[0], i[1], i[1]-i[0]+1, root);
        vector<int> res;
        for(auto i: queries)
        {
            int temp=MinRange(i, root);
            res.push_back((temp<INT_MAX?temp:-1));
        }
        return res;
    }
};
