/*
explanation:

1. we can first sort the intervals and for each query we can push those intervals in which it lies into the min heap
   w.r.t size and right end of the interval and conditons will query[i]>= left side of interval and query[i]<= right side.

2. we can use a hashmap to keep the original index of the queries and then sort the queries array to move from min
   query to max.

3. if the top value of heap lies in the range of ith query then store the size in the result vector else pop out that        value from minheap as i+1th query will also be out of range.

*/
class Solution {
public:
    #define p pair<int,int>
    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {
      int n=intervals.size();

      sort(intervals.begin(),intervals.end());

      unordered_map<int,vector<int>>mp; //to the original index of the queries (there can be duplicates..)

      for(int i=0;i<queries.size();++i)
        mp[queries[i]].push_back(i);

      sort(queries.begin(),queries.end());

      priority_queue<p,vector<p>,greater<p>>pq; //min heap [size,right side of the array]  

      int i=0;

      vector<int>res(queries.size(),-1); //resultant vector

      for(int j=0;j<queries.size();++j){
        while(i<n){
          if(intervals[i][0]<=queries[j] and intervals[i][1]>=queries[j]){  //condition to insert in min heap
              pq.push({intervals[i][1]-intervals[i][0]+1,intervals[i][1]});
              ++i;}
         else if(intervals[i][1]<queries[j]) ++i; //if right value is less than query then move to the next interval
         else break;}

        while(pq.size()>0){
           auto [size,right]=pq.top();
           if(right>=queries[j]){
               for(auto it:mp[queries[j]]) res[it]=size;
               break;}
           else pq.pop();}}

          return res;}};
