**Segment Tree Recursive Solution**

```
Store the min interval values of the given range in Segment tree.
Get the min value of the [Q,Q] range of Segment tree for queries[i] => Q
which gets updated only for the ranges => left <= Q <= right
```

```
class Solution {
public:

    // update min value of the intervals with lazy propagation
    void update(int * tree, int *lazy, int index, int low, int high, int minRange, int maxRange, int val)
    {
        int left = 2 * index + 1;
        int right = left + 1;

        // update lazy value to child and current node
        if(lazy[index]!=INT_MAX)
        {
            tree[index] = min(tree[index],lazy[index]);
            if(low!=high)
            {
                lazy[left] = min(lazy[left],lazy[index]);
                lazy[right] = min(lazy[right],lazy[index]);
            }
            lazy[index] = INT_MAX;
        }

        // no overlap
        if(low > maxRange || high < minRange) return;

        // complete overlap
        if(low>=minRange && high<=maxRange)
        {
            tree[index] = min(tree[index],val);
            lazy[index] = min(lazy[index],val);

            if(low!=high)
            {
                lazy[left] = min(lazy[left],lazy[index]);
                lazy[right] = min(lazy[right],lazy[index]);
            }

            return;
        }

        // partial overlap

        int mid = low + (high-low)/2;

        update(tree,lazy,left,low,mid,minRange,maxRange,val);
        update(tree,lazy,right,mid+1,high,minRange,maxRange,val);
    }

    // query min value of the intervals with lazy propagation    
    int query(int * tree, int *lazy, int index, int low, int high, int range)
    {
        if(low > range || high < range) return INT_MAX;

        int left = 2 * index + 1;
        int right = left + 1;

        if(lazy[index]!=INT_MAX)
        {
            tree[index] = min(tree[index],lazy[index]);
            if(low!=high)
            {
                lazy[left] = min(lazy[left],lazy[index]);
                lazy[right] = min(lazy[right],lazy[index]);
            }
            lazy[index] = INT_MAX;
        }

        // get the range leaf value
        if(low==range && high==range) return tree[index];

        int mid = low + (high-low)/2;

        // only move to the side which contains range

        if(range <= mid) return query(tree,lazy,left,low,mid,range);
        else return query(tree,lazy,right,mid+1,high,range);

    }

    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {

        // get max possible value in the interval and query
        int maxVal = INT_MIN;
        for(int i=0;i<intervals.size();i++)
            maxVal= max(maxVal,intervals[i][1]);

        for(int i=0;i<queries.size();i++)
           maxVal = max(maxVal,queries[i]);

        maxVal++;

        // make max range segment tree and lazy tree
        int *tree = new int[4*maxVal];
        int *lazy = new int[4*maxVal];

        for(int i=0;i<4*maxVal;i++){
            tree[i] = INT_MAX;
            lazy[i] = INT_MAX;
        }

        // update the min interval value for all intervals
        for(int i=0;i<intervals.size();i++)
            update(tree,lazy,0,0,maxVal-1,intervals[i][0],intervals[i][1],intervals[i][1]-intervals[i][0]+1);

        vector<int>res;
        int result;

        // get query min interval value
        for(int i=0;i<queries.size();i++)
        {
            result = query(tree,lazy,0,0,maxVal-1,queries[i]);
            res.push_back(result==INT_MAX ? -1 : result);
        }           

        return res;
    }
};
```


**Segment Tree Iterative Solution**


```
class Solution {
public:

    // update min value of the intervals
    void update(int * tree, int minRange, int maxRange, int val)
    {
        int left = minRange + N;
        int right = maxRange + N;

        while(left <= right)
        {
            if(left&1)
            {
                tree[left] = min(tree[left],val);
                left++;
            }

            if(!(right&1))
            {
                tree[right] = min(tree[right],val);
                right--;
            }

            left = left >> 1;
            right = right >> 1;
        }
    }

    // query min value of the intervals 
    int query(int * tree, int range)
    {
        int index = range + N;
        int res = tree[index];

        while(index > 1)
        {
            index = index >> 1;
            res = min(res,tree[index]);
        }

        return res;
    }

    int N;

    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {

        // get max possible value in the interval and query
        int maxVal = INT_MIN;
        for(int i=0;i<intervals.size();i++)
            maxVal= max(maxVal,intervals[i][1]);

        for(int i=0;i<queries.size();i++)
           maxVal = max(maxVal,queries[i]);

        N = maxVal;
        maxVal++;

        // make max range segment tree
        int *tree = new int[2*maxVal];

        for(int i=0;i<2*maxVal;i++)
            tree[i] = INT_MAX;

        // update the min interval value for all intervals
        for(int i=0;i<intervals.size();i++)
            update(tree,intervals[i][0],intervals[i][1],intervals[i][1]-intervals[i][0]+1);

        vector<int>res;
        int result;

        // get query min interval value
        for(int i=0;i<queries.size();i++)
        {
            result = query(tree,queries[i]);
            res.push_back(result==INT_MAX ? -1 : result);
        }           

        return res;
    }
};
