class Solution {
public:

    // update min value of the intervals
    void update(int * tree, int minRange, int maxRange, int val)
    {
        int left = minRange + N;
        int right = maxRange + N;

        while(left <= right)
        {
            if(left&1)
            {
                tree[left] = min(tree[left],val);
                left++;
            }

            if(!(right&1))
            {
                tree[right] = min(tree[right],val);
                right--;
            }

            left = left >> 1;
            right = right >> 1;
        }
    }

    // query min value of the intervals 
    int query(int * tree, int range)
    {
        int index = range + N;
        int res = tree[index];

        while(index > 1)
        {
            index = index >> 1;
            res = min(res,tree[index]);
        }

        return res;
    }

    int N;

    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {

        // get max possible value in the interval and query
        int maxVal = INT_MIN;
        for(int i=0;i<intervals.size();i++)
            maxVal= max(maxVal,intervals[i][1]);

        for(int i=0;i<queries.size();i++)
           maxVal = max(maxVal,queries[i]);

        N = maxVal;
        maxVal++;

        // make max range segment tree
        int *tree = new int[2*maxVal];

        for(int i=0;i<2*maxVal;i++)
            tree[i] = INT_MAX;

        // update the min interval value for all intervals
        for(int i=0;i<intervals.size();i++)
            update(tree,intervals[i][0],intervals[i][1],intervals[i][1]-intervals[i][0]+1);

        vector<int>res;
        int result;

        // get query min interval value
        for(int i=0;i<queries.size();i++)
        {
            result = query(tree,queries[i]);
            res.push_back(result==INT_MAX ? -1 : result);
        }           

        return res;
    }
};
