let bisect_left=(A,element)=>{
    let lo=0,hi=A.length-1,res=Infinity
    while(lo<=hi){
        let mid=lo+hi>>1
        if(A[mid][0]<element)
            lo=mid+1
        else
            res=Math.min(res,mid),
            hi=mid-1
    }
    return res
}
let bisect_right=(A,element)=>{
    let lo=0,hi=A.length-1,res=-Infinity
    while(lo<=hi){
        let mid=lo+hi>>1
        if(A[mid][0]>element)
            hi=mid-1
        else
            res=Math.max(res,mid),
            lo=mid+1
    }
    return res
}
var minInterval = function(I, Q) {
    I.sort((a,b)=>a[1]-a[0]-b[1]+b[0])
    let Queries=[...Q]
    Queries=Queries.map((d,i)=>[d,i]).sort((a,b)=>a[0]-b[0])
    let n=Q.length,ans=[...Array(n)].map(d=>-1),par=[...Array(n)].map((d,i)=>i)
    let find=x=>par[x]!==x?par[x]=find(par[x]):par[x]
    for(let [a,b] of I){
        let [l,r]=[bisect_left(Queries,a),bisect_right(Queries,b)] //indices
        let v=find(l)
        while(v<=r)
            ans[v]=b-a+1,
            par[v]=v+1,
            v=find(v)
    }
    for(let i in Queries){
        let [val,originalIdx]=Queries[i]
        Q[originalIdx]=ans[i]
    }
    return Q
};
