type Interval struct {
	left  int
	right int
	size  int
}

func (self *Interval) GetSize() int {
	return self.right - self.left + 1
}

func NewInterval(a []int) Interval {
	return Interval{
		left:  a[0],
		right: a[1],
		size:  a[1] - a[0] + 1,
	}

}

type IntervalHeap []Interval

func (self IntervalHeap) Len() int {
	return len(self)
}

func (self IntervalHeap) Less(a, b int) bool {
	return self[a].size < self[b].size
}
func (self IntervalHeap) Swap(a, b int) {
	self[a], self[b] = self[b], self[a]
}
func (h *IntervalHeap) Push(x interface{}) {
	*h = append(*h, x.(Interval))
}

func (h *IntervalHeap) Pop() interface{} {
	old := *h
	n := len(old)
	x := old[n-1]
	*h = old[0 : n-1]
	return x
}

func minInterval(intervals [][]int, queries []int) []int {
	sort.Slice(intervals, func(i, j int) bool {
		return intervals[i][0] < intervals[j][0]
	})
	queries2 := make([]int, len(queries))
	copy(queries2, queries)
	sort.Ints(queries2)
	ans := make(map[int]int)

	h := &IntervalHeap{}

	heap.Init(h)
	idx := 0 //intervals index
	for _, query := range queries2 {
		for ; idx < len(intervals) && intervals[idx][0] <= query; idx++ {
			heap.Push(h, NewInterval(intervals[idx]))
		}

		for h.Len() > 0 && (*h)[0].right < query {
			heap.Pop(h)
		}
		if h.Len() == 0 {
			ans[query] = -1
		}
		if h.Len() > 0 {
			ans[query] = (*h)[0].size
		}

	}
	ret := make([]int, len(queries))

	for i, n := range queries {
		ret[i] = -1
		if a, ok := ans[n]; ok {
			ret[i] = a
		}

	}
	return ret
}

