class Solution {
public:
    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {
        vector<Node> nodes;
        for(int i = 0; i < intervals.size(); i++) {
            nodes.emplace_back(0, intervals[i][0], i);
            nodes.emplace_back(2, intervals[i][1], i);
        }
        for(int i = 0; i < queries.size(); i++) {
            nodes.emplace_back(1, queries[i], i);
        }

        sort(nodes.begin(), nodes.end(), [](const Node& n1, const Node& n2){
            if(n1.value == n2.value) {
                return n1.kind < n2.kind;
            } else {
                return n1.value < n2.value;
            }
        });

        set<Interval> mySet;
        vector<set<Interval>::iterator> its(intervals.size());
        vector<int> ret(queries.size());
		// Sweep line
        for(int i = 0; i < nodes.size(); i++) {
            if(nodes[i].kind == 0) {
                //  meet start: insert interval to data structure
                int s = nodes[i].value;
                int intervalIndex = nodes[i].index;
                int e = intervals[intervalIndex][1];
                auto it = mySet.insert(Interval(e - s + 1, intervalIndex)).first;
                its[intervalIndex] = it;
            } else if (nodes[i].kind == 1) {
                // meet query: find the shortest interval in data structure
                int queryIndex = nodes[i].index;
                int q = nodes[i].value;
                if(mySet.empty())
                    ret[queryIndex] = -1;
                else {
                    auto it = mySet.begin();
                    ret[queryIndex] = it->size;
                }
            } else {
                // meet end of interval: 
                // remove interval from data structure
                int intervalIndex = nodes[i].index;
                auto it = its[intervalIndex];
                mySet.erase(it);
            }
        }

        return ret;
    }
};
