class Solution:
    def minInterval(self, intervals: List[List[int]], queries: List[int]) -> List[int]:
        intervals.sort(reverse=True)

        queries_ind = sorted([(q, i) for i, q in enumerate(queries)])

        out = [0 for _ in queries]
        heap = []
        for q, i in queries_ind:
            while len(intervals) != 0 and q >= intervals[-1][0]:
                s, e = intervals.pop()
                heapq.heappush(heap, [e-s+1, e])

            while len(heap) != 0 and heap[0][1] < q:
                heapq.heappop(heap)

            if len(heap) == 0:
                out[i] = -1
            else:
                out[i] = heap[0][0]

        return out
