class Solution {
    Map<Integer,Integer>f=new HashMap<>();
    public int[] minInterval(int[][] A, int[] queries) {
        int q[][]=new int[queries.length][2];
        int res[]=new int[q.length];
        Arrays.fill(res,-1);

        for(int i=0;i<q.length;i++){
            q[i][0]=queries[i];
            q[i][1]=i;
        }

        Arrays.sort(q,(a,b)->{
            return a[0]-b[0];
        });

        Arrays.sort(A,(a,b)->{
            return a[0]-b[0];
        });


        int j=0;
        PriorityQueue<int[]>pq=new PriorityQueue<>((a,b)->{
            return a[1]-b[1];
        }); //sort based on the end
        PriorityQueue<Integer>min=new PriorityQueue<>();//heap storing the size

        for(int i=0;i<q.length;i++){
            int val=q[i][0],index=q[i][1];
            while(j<A.length&&A[j][0]<=val){
                if(A[j][1]>=val){
                    //add 
                    int size=A[j][1]-A[j][0]+1;
                    pq.add(A[j]);
                    put(size);
                    min.add(size);
                }
                j++;
            }

            while(pq.size()>0&&pq.peek()[1]<val){//pq should contain all those interval that include the current query number
                int top[]=pq.poll();
                int size=top[1]-top[0]+1;
                remove(size);
            }

            while(min.size()>0&&!f.containsKey(min.peek()))min.poll();

            if(min.size()>0){//choose the minimum size
                res[index]=min.peek();
            }

        }

        return res;
    }

    public void put(int k){
        if(!f.containsKey(k))f.put(k,1);
        else f.put(k,f.get(k)+1);
    }

    public void remove(int k){
        f.put(k,f.get(k)-1);
        if(f.get(k)==0)f.remove(k);
    }
}
