# Code
```
class Solution {
public:
    vector<int> minInterval(vector<vector<int>>& it, vector<int>& pr)
    {
        int n=it.size();
        int m=pr.size();
        vector<int>v(m,0);
        vector<int>qr=pr;
        unordered_map<int,vector<pair<int,int>>>mp;
        unordered_map<int,int>ans;
        sort(qr.begin(),qr.end());
        for(int i=0;i<n;i++)
        {
            int x=(it[i][1]-it[i][0])+1;
            auto start=lower_bound(qr.begin(),qr.end(),it[i][0])-qr.begin();
            auto end=upper_bound(qr.begin(),qr.end(),it[i][1])-qr.begin();
            if(start<=(end-1))
            {
                mp[start].push_back({x,(end-1)});
            }
        }
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;
        for(int i=0;i<m;i++)
        {
            for(int j=0;j<mp[i].size();j++)
            {
                pq.push(mp[i][j]);
            }
            while((!pq.empty())&&(i>pq.top().second))
            {
                pq.pop();
            }
            if(pq.empty())
            {
                ans[qr[i]]=-1;
            }
            else
            {
                ans[qr[i]]=pq.top().first;
            }
        }
        for(int i=0;i<m;i++)
        {
            v[i]=ans[pr[i]];
        }
        return v;
    }
};
```

