use std::collections::{BinaryHeap, HashMap};
use std::cmp::Ordering;

#[derive(Debug, PartialEq, Eq)]
struct Interval {
    low: i32,
    high: i32,
}

impl Ord for Interval {
    fn cmp(&self, other: &Self) -> Ordering {
        if self.high - self.low == other.high - other.low {
            return self.high.cmp(&other.high).reverse();
        }

        return (self.high - self.low).cmp(&(other.high - other.low)).reverse();
    }
}

impl PartialOrd for Interval {
    fn partial_cmp(&self, other: &Self) -> Option<Ordering> {
        Some(self.cmp(other))
    }
}

impl Solution {
    pub fn min_interval(mut intervals: Vec<Vec<i32>>, mut queries: Vec<i32>) -> Vec<i32> {
        // 1. Sort intervals by left border
        intervals.sort_by(|a, b| if a[0] == b[0] { a[1].cmp(&b[1]) } else { a[0].cmp(&b[0]) });
        let mut i = 0;

        let mut heap = BinaryHeap::new();
        let mut result_map = HashMap::new();

        // 2. Sort queries
        let mut sorted_queries = queries.clone();
        sorted_queries.sort();
        for query in sorted_queries {
            // 3. Add intervals to the heap only if the current query is not less than the left border
            while i < intervals.len() && intervals[i][0] <= query {
                heap.push(Interval { low: intervals[i][0], high: intervals[i][1] });
                i += 1;
            }

            loop {
                // 4. Get intervals from the heap (sorted by length) and pop in they not usable anymore
                if let Some(top_interval) = heap.peek() {
                    if top_interval.low <= query && query <= top_interval.high {
                        result_map.insert(query, top_interval.high - top_interval.low + 1);
                        break;
                    } else {
                        heap.pop();
                    }
                } else {
                    result_map.insert(query, -1);
                    break;
                }
            }
        }

        // 5. Recover the original order of the queries
        return queries.into_iter().map(|x| result_map[&x]).collect();
    }
}
