class MinHeap {
    public heap;
    constructor() {
        this.heap = [];
    }

    _heapifyUp(index: number) {
        while (index > 0) {
            const parentIndex: number = Math.floor((index - 1) / 2);
            if (this.heap[index][0] < this.heap[parentIndex][0]) {
                [this.heap[index], this.heap[parentIndex]] = [this.heap[parentIndex], this.heap[index]];
                index = parentIndex;
            } else {
                break;
            }
        }
    }

    _heapifyDown(index: number) {
        while (index < this.heap.length) {
            const left: number = (index * 2) + 1;
            const right: number = (index * 2) + 2;
            let smallest = index;
            if (this.heap[left] && this.heap[left][0] < this.heap[smallest][0]) {
                smallest = left;
            }
            if (this.heap[right] && this.heap[right][0] < this.heap[smallest][0]) {
                smallest = right;
            }
            if (smallest !== index) {
                [this.heap[index], this.heap[smallest]] = [this.heap[smallest], this.heap[index]];
                index = smallest;
            } else {
                break;
            }
        }
    }

