class Solution {
public:
    vector<int> minInterval(vector<vector<int>>& v, vector<int>& queries) {
        int n=queries.size();
        vector<int> ans(n,-1);
        sort(v.begin(),v.end());

        vector<pair<int,int>> q(n);

        for(int i=0;i<n;i++)
        {
            q[i]={queries[i],i};
        }

        sort(q.begin(),q.end());

        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> pq;

         for(int i=0,j=0;i<q.size();i++)
          {
            while(j<v.size() && v[j][0]<=q[i].first)
            {
                pq.push({v[j][1]-v[j][0]+1,v[j][1]});
                j++;
            }
            while(pq.size() && pq.top().second<q[i].first)
            {
                pq.pop();
            }

            if(pq.size()!=0)
            {
                ans[q[i].second]=pq.top().first;
            }
            else
