**1. Sort and iterate, using the `std::multiset`**
```
/**
 * Time Complexity: O((n_intervals + n_queries) * log(n_intervals + n_queries))
 * Space Complexity: O(n_intervals + n_queries)
 * where `n_intervals` is the length of the vector `intervals`
 *       `n_queries` is the length of the vector `queries`
 */
class Solution {
 private:
  enum class EventType : uint8_t {
    start,
    query,
    end
  };

  /**
   * {
   *   the timestamp,
   *   the type,
   *   the value
   * }
   *
   * the `type` will have 3 options,
   * 1. EventType::start
   *    which means the `timestamp` stands for the starting timestamp of an interval
   *                the `value` is the size of the corresponding interval
   * 2. EventType::query
   *    which means the `timestamp` stands for an element of the vecotr `queries`
   *                the `value` is the index of the element in the vector `queries`
   * 3. EventType::end
   *    which means the `timestamp` stands for the ending timestamp of an interval
   *                the `value` is the size of the corresponding interval
   */
  using event_t = tuple<int, EventType, int>;

 public:
  vector<int> minInterval(const vector<vector<int>> &intervals, const vector<int> &queries) {
    constexpr int invalid_interval_size = -1;
    vector<event_t> events;
    for (const vector<int> &interval : intervals) {
      events.emplace_back(interval.front(), EventType::start, interval.back() - interval.front() + 1);
      events.emplace_back(interval.back(), EventType::end, interval.back() - interval.front() + 1);
    }
    const int n_queries = static_cast<int>(queries.size());
    for (int i_query = 0; i_query < n_queries; ++i_query) {
      events.emplace_back(queries[i_query], EventType::query, i_query);
    }
    sort(events.begin(), events.end());

    vector<int> ret(n_queries, invalid_interval_size);
    multiset<int> interval_sizes;
    for (const auto [_, type, value] : events) {
      if (type == EventType::start) {
        interval_sizes.emplace(value);
      } else if (type == EventType::end) {
        interval_sizes.erase(interval_sizes.find(value));
      } else {
        // type == EventType::query
        ret[value] = interval_sizes.empty() ? invalid_interval_size : *interval_sizes.begin();
      }
    }
    return ret;
  }
};
```
**2. Use the `std::priority_queue`**
```
/**
 * Time Complexity: O(n_intervals * log(n_intervals) + n_queries * log(n_queries))
 * Space Complexity: O(n_intervals + n_queries)
 * where `n_intervals` is the length of the vector `intervals`
 *       `n_queries` is the length of the vector `queries`
 */
class Solution {
 public:
  vector<int> minInterval(vector<vector<int>> &intervals, const vector<int> &queries) {
    using pq_node_t = pair<int, int>;   // {the size of the interval, the ending timestamp of the interval}
    constexpr int invalid_interval_size = -1;
    const int n_intervals = static_cast<int>(intervals.size());
    const int n_queries = static_cast<int>(queries.size());
    sort(intervals.begin(), intervals.end());
    int query_indices[n_queries];
    iota(query_indices, query_indices + n_queries, 0);
    sort(query_indices, query_indices + n_queries, [&queries](const int lhs, const int rhs) -> bool {
      return queries[lhs] < queries[rhs];
    });
    priority_queue<pq_node_t, vector<pq_node_t>, greater<>> pq;
    vector<int> ret(n_queries, invalid_interval_size);
    for (int i_interval = 0, i = 0; i < n_queries; ++i) {
      const int i_query = query_indices[i];
      const int query = queries[i_query];
      for (; i_interval < n_intervals && intervals[i_interval].front() <= query; ++i_interval) {
        if (intervals[i_interval].back() >= query) {
          pq.emplace(intervals[i_interval].back() - intervals[i_interval].front() + 1,
                     intervals[i_interval].back());
        }
      }
      while (!pq.empty() && pq.top().second < query) {
        pq.pop();
      }
      ret[i_query] = pq.empty() ? invalid_interval_size : pq.top().first;
    }
    return ret;
  }
};
```
**3. Use the `std::multimap`**
```
/**
 * Time Complexity: O(n_intervals * log(n_intervals) + n_queries * log(n_queries))
 * Space Complexity: O(n_queries)
 * where `n_intervals` is the length of the vector `intervals`
 *       `n_queries` is the length of the vector `queries`
 */
class Solution {
 public:
  vector<int> minInterval(vector<vector<int>> &intervals, const vector<int> &queries) {
    constexpr int invalid_interval_size = -1;
    const int n_queries = static_cast<int>(queries.size());
    sort(intervals.begin(), intervals.end(), [](const vector<int> &lhs, const vector<int> &rhs) -> bool {
      return lhs.back() - lhs.front() < rhs.back() - rhs.front();
    });

    multimap<int, int> query_to_index;
    for (int i_query = 0; i_query < n_queries; ++i_query) {
      query_to_index.emplace(queries[i_query], i_query);
    }

    vector<int> ret(n_queries, invalid_interval_size);
    for (const vector<int> &interval : intervals) {
      const int size = interval.back() - interval.front() + 1;
      for (auto itr = query_to_index.lower_bound(interval.front());
           itr != query_to_index.end() && itr->first <= interval.back();
           itr = query_to_index.erase(itr)) {
        ret[itr->second] = size;
      }
    }
    return ret;
  }
};
```

