   *                the `value` is the index of the element in the vector `queries`
   * 3. EventType::end
   *    which means the `timestamp` stands for the ending timestamp of an interval
   *                the `value` is the size of the corresponding interval
   */
  using event_t = tuple<int, EventType, int>;

 public:
  vector<int> minInterval(const vector<vector<int>> &intervals, const vector<int> &queries) {
    constexpr int invalid_interval_size = -1;
    vector<event_t> events;
    for (const vector<int> &interval : intervals) {
      events.emplace_back(interval.front(), EventType::start, interval.back() - interval.front() + 1);
      events.emplace_back(interval.back(), EventType::end, interval.back() - interval.front() + 1);
    }
    const int n_queries = static_cast<int>(queries.size());
    for (int i_query = 0; i_query < n_queries; ++i_query) {
      events.emplace_back(queries[i_query], EventType::query, i_query);
    }
    sort(events.begin(), events.end());

    vector<int> ret(n_queries, invalid_interval_size);
    multiset<int> interval_sizes;
    for (const auto [_, type, value] : events) {
      if (type == EventType::start) {
        interval_sizes.emplace(value);
      } else if (type == EventType::end) {
        interval_sizes.erase(interval_sizes.find(value));
      } else {
        // type == EventType::query
        ret[value] = interval_sizes.empty() ? invalid_interval_size : *interval_sizes.begin();
      }
    }
    return ret;
  }
};
