/**
 * Time Complexity: O(n_intervals * log(n_intervals) + n_queries * log(n_queries))
 * Space Complexity: O(n_intervals + n_queries)
 * where `n_intervals` is the length of the vector `intervals`
 *       `n_queries` is the length of the vector `queries`
 */
class Solution {
 public:
  vector<int> minInterval(vector<vector<int>> &intervals, const vector<int> &queries) {
    using pq_node_t = pair<int, int>;   // {the size of the interval, the ending timestamp of the interval}
    constexpr int invalid_interval_size = -1;
    const int n_intervals = static_cast<int>(intervals.size());
    const int n_queries = static_cast<int>(queries.size());
    sort(intervals.begin(), intervals.end());
    int query_indices[n_queries];
    iota(query_indices, query_indices + n_queries, 0);
    sort(query_indices, query_indices + n_queries, [&queries](const int lhs, const int rhs) -> bool {
      return queries[lhs] < queries[rhs];
    });
    priority_queue<pq_node_t, vector<pq_node_t>, greater<>> pq;
    vector<int> ret(n_queries, invalid_interval_size);
    for (int i_interval = 0, i = 0; i < n_queries; ++i) {
      const int i_query = query_indices[i];
      const int query = queries[i_query];
      for (; i_interval < n_intervals && intervals[i_interval].front() <= query; ++i_interval) {
        if (intervals[i_interval].back() >= query) {
          pq.emplace(intervals[i_interval].back() - intervals[i_interval].front() + 1,
                     intervals[i_interval].back());
        }
      }
      while (!pq.empty() && pq.top().second < query) {
        pq.pop();
      }
      ret[i_query] = pq.empty() ? invalid_interval_size : pq.top().first;
    }
    return ret;
  }
};
