class Solution {
 public:
  vector<int> minInterval(vector<vector<int>> &intervals, const vector<int> &queries) {
    constexpr int invalid_interval_size = -1;
    const int n_queries = static_cast<int>(queries.size());
    sort(intervals.begin(), intervals.end(), [](const vector<int> &lhs, const vector<int> &rhs) -> bool {
      return lhs.back() - lhs.front() < rhs.back() - rhs.front();
    });

    multimap<int, int> query_to_index;
    for (int i_query = 0; i_query < n_queries; ++i_query) {
      query_to_index.emplace(queries[i_query], i_query);
    }

    vector<int> ret(n_queries, invalid_interval_size);
    for (const vector<int> &interval : intervals) {
      const int size = interval.back() - interval.front() + 1;
      for (auto itr = query_to_index.lower_bound(interval.front());
           itr != query_to_index.end() && itr->first <= interval.back();
           itr = query_to_index.erase(itr)) {
        ret[itr->second] = size;
      }
    }
    return ret;
  }
};
