class Solution {
    // Nested class representing a node in the interval tree
    private class Node {
        public int left, right, min;
        public Node lnode, rnode;

        public Node(int l, int r) {
            this.left = l;
            this.right = r;
            this.min = Integer.MAX_VALUE;
        }
    }

    // Function to find the minimum interval containing the query point
    public int find(Node curr, int q) {
        // If the current node is null or the query point is outside the range of the current node,
        // return the maximum value to indicate that no interval contains the query point
        if (curr == null || q < curr.left || q > curr.right)
            return Integer.MAX_VALUE;

        int m = (curr.left + curr.right) / 2;

        // If the query point is in the left subtree, recursively search in the left subtree
        // and return the minimum value found
        if (q <= m)
            return Integer.min(find(curr.lnode, q), curr.min);
        // If the query point is in the right subtree, recursively search in the right subtree
        // and return the minimum value found
        else
            return Integer.min(find(curr.rnode, q), curr.min);
    }

    // Function to add an interval to the interval tree
    public void add(Node curr, int l, int r, int b) {
        // If the interval is outside the range of the current node, return
        if (l < curr.left || r > curr.right)
            return;

        // If the interval exactly matches the range of the current node,
        // update the minimum value of the node and return
        if (curr.left == l && curr.right == r) {
            curr.min = Integer.min(curr.min, b);
            return;
        }

        int m = (curr.left + curr.right) / 2;

        // If the left child of the current node is null, create the left and right children
        if (curr.lnode == null) {
            curr.lnode = new Node(curr.left, m);
            curr.rnode = new Node(m + 1, curr.right);
        }

        // Recursively add the interval to the appropriate child node
        if (r <= m)
            add(curr.lnode, l, r, b);
        else if (l >= m + 1)
            add(curr.rnode, l, r, b);
        else {
            add(curr.lnode, l, m, b);
            add(curr.rnode, m + 1, r, b);
        }
    }

    // Main function to find the minimum intervals for given queries
    public int[] minInterval(int[][] intervals, int[] queries) {
        // Create the root node of the interval tree with a large range
