class Solution {
    class Pair{
        public int st,end;
        public Pair(int s,int e){
            this.st=s;
            this.end=e;
        }
    }
    public int[] minInterval(int[][] intervals, int[] queries) {
        Arrays.sort(intervals,(i1,i2)->Integer.compare(i1[1]-i1[0],i2[1]-i2[0]));
        int [] ans=new int[queries.length];
        Arrays.fill(ans,-1);
        TreeSet<Pair> ts=new TreeSet<>((p1,p2)->{
            if(p1.st==p2.st)
                return  Integer.compare(p1.end,p2.end);
            else 
                 return  Integer.compare(p1.st,p2.st);
        }
        );
        for(int i=0;i<queries.length;i++)ts.add(new Pair(queries[i],i));
        for(int inter[]:intervals){
            Iterator<Pair> it= ts.tailSet(new Pair(inter[0],-1)).iterator();
            while(it.hasNext()){
                Pair res=it.next();
                if(res.st<=inter[1]){
                    ans[res.end]=inter[1]-inter[0]+1;
                    it.remove();
                }else break;
            }
        }
        return ans;
    }
}
