public class Solution {
    public int[] MinInterval(int[][] intervals, int[] queries) {
        int queriesLength = queries.Length;
        // we have to store queries with their original indexes
        int[][] queriesWithIndexes = new int[queriesLength][];

        int index = 0;
        foreach(var query in queries){
            queriesWithIndexes[index] = new int[2] { query, index++ };
        }

        Array.Sort(queriesWithIndexes, (first, second) => first[0] - second[0]);
        Array.Sort(intervals, (first, second) => first[0] - second[0]);

        // first element will be with the largest length (priority)
        var queue = new PriorityQueue<int[], int>();
        var result = new int[queriesLength];

        index = 0;
        foreach(var queryWithIndex in queriesWithIndexes){
            // query is great or equal than start of intervals[index]
            while(index < intervals.Length && intervals[index][0] <= queryWithIndex[0]){
                int itemsCount = intervals[index][1] - intervals[index][0] + 1;
                queue.Enqueue(new int[2] { itemsCount, intervals[index][1]}, itemsCount);
                index++;
            }

            // remove intervals which end point is less than current query
            while(queue.Count > 0 && queue.Peek()[1] < queryWithIndex[0]){
                queue.Dequeue();
            }

            result[queryWithIndex[1]] = queue.Count > 0 ? queue.Peek()[0] : -1;
        }

        return result;
    }
}
