class Solution {
public:
typedef pair<int,int> PI;
    vector<int> minInterval(vector<vector<int>>& itl, vector<int>& q) {
        int n=itl.size();
        int m=q.size();
        vector<int> sizes(n);   // storing sizes of each interval
        sort(itl.begin(),itl.end());
        vector<int> ans(m);
        int* arr=new int[10000001];
        int mini=itl[0][0];
        int maxi=itl[n-1][1];
        set<pair<int,int>> st;
        priority_queue<PI,vector<PI>,greater<PI>> pq;
        for(auto& ele:  itl){
            maxi=max(maxi,ele[1]);
        }
        int j=0;
        for(int i=mini;i<=maxi;i++){
            while(j<n){
                if(i<itl[j][0]) break;
                sizes[j]=itl[j][1]-itl[j][0]+1;
                st.insert(make_pair(sizes[j],j));
                pq.push(make_pair(itl[j][1],j));
                j++;
            }
            arr[i]=(st.size())?(st.begin()->first):(-1);
            while(pq.size() && pq.top().first==i){
                auto [a,b]=pq.top();
                st.erase({sizes[b],b});
                pq.pop();
            }
        }

        for(int j=0;j<m;j++){
            if(q[j]<mini  || q[j]>maxi) ans[j]=-1;
            else ans[j]=arr[q[j]];
        }
        return ans;
    }
};
