public class Solution {
    public int[] MinInterval(int[][] intervals, int[] queries) {
        int n=queries.Length;
        int[] q=Enumerable.Range(0,n).ToArray();
        Array.Sort(q,(x,y)=>queries[x].CompareTo(queries[y]));

        int[] res=new int[n];

        Array.Sort(intervals,(x,y)=> x[0].CompareTo(y[0]) );
        int j=0;
        PriorityQueue<int,int> pq=new PriorityQueue<int,int>();
        for(int i=0;i<n;i++)
        {
            while(j<intervals.Length && intervals[j][0]<=queries[q[i]]){
                pq.Enqueue(j, intervals[j][1] -intervals[j][0]+1);
                j++;
            }

            while(pq.Count>0 && intervals[pq.Peek()][1] <queries[q[i]])
                pq.Dequeue();

            res[q[i]]=pq.Count >0 ? intervals[pq.Peek()][1]-intervals[pq.Peek()][0]+1 : -1;
        }
        return res;
    }
}
