class Solution {
public:
    std::vector<int> minInterval(std::vector<std::vector<int>> & intervals, std::vector<int> & queries) {
        std::vector<std::tuple<int, int, int>> points;
        points.reserve(std::size(intervals) + std::size(queries));
        for (auto & i : intervals)
            points.emplace_back(i[0], 0, i[1] - i[0] + 1), points.emplace_back(i[1], 2, i[1] - i[0] + 1);
        for (int i = 0; i < std::size(queries); ++i)
            points.emplace_back(queries[i], 1, i);
        std::sort(std::begin(points), std::end(points), [] (auto & a, auto & b) noexcept { return std::get<0>(a) < std::get<0>(b) || std::get<0>(a) == std::get<0>(b) && std::get<1>(a) < std::get<1>(b); });

        std::vector<int> result(std::size(queries));
        std::map<int, int> sizes;

        for (auto & [x, type, val] : points) {
            switch (type) {
                case 0: // starting interval (val is interval length)
                    ++sizes[val];
                    break;
                case 1: // query point (val is result index)
                    result[val] = std::empty(sizes) ? -1 : std::begin(sizes)->first;
                    break;
                case 2: // ending interval (val is interval length)
                    if (!--sizes[val])
                        sizes.erase(val);
                    break;
            }
        }

        return result;
    }
};
