impl Solution {
    pub fn min_interval(intervals: Vec<Vec<i32>>, queries: Vec<i32>) -> Vec<i32> {
        let mut intervals = intervals;
        intervals.sort();
        let mut ids = (0..queries.len()).collect::<Vec<usize>>();
        ids.sort_by_key(|&i| queries[i]);
        let mut s = std::collections::BTreeSet::new();
        let mut res = vec![-1; queries.len()];
        let mut j = 0;
        for i in ids {
            while j < intervals.len() && intervals[j][0] <= queries[i] {
                s.insert((intervals[j][1] - intervals[j][0] + 1, intervals[j][1]));
                j += 1;
            }
            while !s.is_empty() && s.iter().next().unwrap().1 < queries[i] {
                let v = *s.iter().next().unwrap();
                s.remove(&v);
            }
            if !s.is_empty() {
                res[i] = s.iter().next().unwrap().0;
            }
        }
        res
    }
}
