        set<pair<int,int>> QS(Q.begin(),Q.end());

        vector<int> res(n,-1);  

        for(int i=0;i<intervals.size() && !QS.empty();i++){

            int start=intervals[i][0];
            int end=intervals[i][1];
            int range=end-start+1;

            auto itr1=QS.lower_bound({start,-1});
