class Solution {
public:
    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {
        int n = queries.size(), handled = 0;

        vector<int> res(n, -1);
        set<pair<int,int>> queriesWithPos;

        // sort intervals based on size
        sort(intervals.begin(), intervals.end(), [](const vector<int>& a, const vector<int>& b){
            return a[1]-a[0] < b[1]-b[0];
        });

        // use set to store the queries in increasing order, also store thier index
        for(int i=0;i<n;i++) queriesWithPos.insert({queries[i], i});

        for(auto interval: intervals) {
            int st = interval[0], end = interval[1];

            // for the smallest interval, update the answer for all the queries which fall inside it
            auto it = queriesWithPos.lower_bound({st, 0});
            while(it!=queriesWithPos.end() and it->first <= end) {
                res[it->second] = (end - st + 1);
                // remove the query from set once found its answer
                queriesWithPos.erase(it++);
            }
        }

        return res;
    }
};
