class Solution {
public:
    #define ll long long

    static bool cmp(pair<ll,ll>&p1,pair<ll,ll>&p2){
        if(p1.first==p2.first){
            return p1.second>p2.second;
        }
        return p1.first<p2.first;
    }

    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {
        ll n=intervals.size(),m=queries.size(),diff,i=0,j=0;
        vector<pair<ll,ll>>new_queries,points;
        vector<int>answer(m);
        for(ll i=0;i<m;i++){
            new_queries.push_back({queries[i],i});
        }
        for(ll i=0;i<n;i++){
            diff=intervals[i][1]-intervals[i][0]+1;
            points.push_back({intervals[i][0],diff});
            points.push_back({intervals[i][1],-diff});
        }
        sort(points.begin(),points.end(),cmp);
        sort(new_queries.begin(),new_queries.end());
        map<ll,ll>mp;
        while(j<m){
            while(points[i].first<=new_queries[j].first && i<points.size()){
                if(points[i].second<0 && points[i].first==new_queries[j].first){
                    break;
                }
                if(points[i].second>0){
                    mp[points[i].second]++;
                }
                else{
                    mp[-points[i].second]--;
                    if(mp[-points[i].second]==0){
                        mp.erase(-points[i].second);
                    }
                }
                i++;
            }
            if(mp.size()==0){
                answer[new_queries[j].second]=-1;
            }
            else{
                auto temp=mp.lower_bound(0);
                answer[new_queries[j].second]=temp->first;
            }
            j++;
        }
        return answer;
    }
};
