class Solution {

    public int[] minInterval(int[][] intervals, int[] queries) {
        int ql = queries.length;
        int len = intervals.length;
        int[] tmp = new int[ql];
        for(int i=0;i<ql;i++){
            tmp[i]=queries[i];
        }
        Map<Integer,Integer> qmap = new HashMap<>();

        Arrays.sort(queries);
        Arrays.sort(intervals, (int[] a, int[] b)->{
            if(a[0]==b[0]){
                return a[1]-b[1];
            }
            return a[0]-b[0]; 
        });

        Queue<int[]> pq = new PriorityQueue<>((int[] a,int[] b)->{
            if(a[0]==b[0]){
                return a[1]-b[1];
            }
            return a[0]-b[0];
        });
        int i=0,j=0;
        while(j<ql){    
            while(i<len && intervals[i][0]<=queries[j]){
                int l=intervals[i][0];
                int r=intervals[i][1];
                int diff = r-l+1;
                int[] arr={diff,r};
                pq.add(arr);
                i++;
            }
            while(!pq.isEmpty() && pq.peek()[1]<queries[j]){
                pq.poll();
            }
            if(!pq.isEmpty()){
                qmap.put(queries[j],pq.peek()[0]);
