```
class Solution {
public:
    vector<int> minInterval(vector<vector<int>>& intervals, vector<int>& queries) {
        // sorting queries in ascending order allows us to discard intervals smaller than pervious queries
        vector<int> Q = queries; sort(Q.begin(), Q.end()); 
        // sorting intervals in ascending order helps reduce number of elements to traverse for increasing query values
        vector<vector<int>> I = intervals; sort(I.begin(), I.end());

        // store interval size(-ve, as a pq by default is a max heap : helps keep smallest interval on top)
        priority_queue<pair<int, int>> pq; 
        // As a sorted order of queries might cause a change in order, we temporarily store a <query, result> map
        unordered_map<int, int> store; 

        // i is an interator on the sorted intervals array
        int i = 0, n = I.size();
        for(auto q : Q){
            // Add all intervals which might possible include the query (starting element less than or equal to query)
            while(i < n && I[i][0] <= q)
                pq.push({-(I[i][1]-I[i][0]+1), I[i++][1]});
            // discard added/previously added intervals which are smaller than current query
            while(pq.size() && pq.top().second < q)
                pq.pop();
            // In case the pq is not empty, the top element represents interval with the smallest length 
            // containing current query
            store[q] = (pq.size()) ? -pq.top().first : -1;
        }

        // return result by storing query results in original order:
        vector<int> res;
        for(auto q : queries) res.push_back(store[q]);
        return res;
    }
};
