class Solution {
public:
    vector<int> minInterval(vector<vector<int>>& in, vector<int>& q) {
        set<pair<int,int>> st;
        for(int i=0;i<q.size();i++)st.insert({q[i],i});
        sort(in.begin(),in.end(),[](vector<int> &a,vector<int> &b){
            return a[1]-a[0]<b[1]-b[0];
        });
        int n=in.size();
        int m=q.size();
        vector<int> ans(m,-1);
        for(int i=0;i<n;i++){
            vector<pair<int,int>> v;
            auto it=st.lower_bound({in[i][0],0});
            while(it!=st.end() and (*it).first>=in[i][0] and (*it).first<=in[i][1]){
                ans[(*it).second]=in[i][1]-in[i][0]+1;
                v.push_back(*it);
                it++;
            }
            for(int i=0;i<v.size();i++){
                st.erase(v[i]);
            }
        }
        return ans;
    }
};
