function minInterval(intervals: number[][], queries: number[]): number[] {
	// sort intervals according to start
    intervals.sort((a, b) => a[0] - b[0]);
	// create sorted queries
    let queriesSorted = [...queries].sort((a,b) => a - b);
	// cache to map query to result  
    const cache:Map<number, number> = new Map();

	// min priority queue to map distance to interval end where interval distance is use to evaluate priority;
    const priorityQueue = new MinPriorityQueue();
    let i = 0;
    for(const q of queriesSorted){
        if(cache.has(q)){
            continue;
        }
        while(i < intervals.length && intervals[i][0] <= q){
            const [start, end] = intervals[i];

            priorityQueue.enqueue(end, (end+1) -  start);
            i++
        }
        // remove invalid intervals min, i.e. intervals with end less than the query;
        while(priorityQueue.size() > 0 && priorityQueue.front().element < q){
            priorityQueue.dequeue()
        }
		// set query value to  minimum distance in  queue
        cache.set(q, priorityQueue.front()?.priority ?? -1);
    }


    return queries.map(v => cache.get(v));
};
