class MinHeap {
    constructor() {
        this.values = [];
    }

    extractMinimum() {
        const minimum = this.values[0]
        const lastElem = this.values.pop();

        if (this.values.length) {
            this.values[0] = lastElem;
            this.sinkDown();
        }

        return minimum;
    }

    swap(index1, index2) {
        [this.values[index1], this.values[index2]] = [this.values[index2], this.values[index1]];
    }

    insert(value) {
        this.values.push(value);
        this.bubbleUp();
    }

    bubbleUp() {
        let index = this.values.length - 1;

        while (index > 0) {
            let parentIndex = Math.floor((index - 1) / 2);

            if ((this.values[parentIndex][0] < this.values[index][0])
            || (this.values[parentIndex][0] === this.values[index][0] && 
                this.values[parentIndex][1][1] <= this.values[index][1][1]))
             {
                break;
            }

            this.swap(index, parentIndex);
            index = parentIndex;
        }
    }

    sinkDown() {
        let index = 0;

        while (true) {  
            let swapIndex = index;
            let leftCI = 2 * index + 1;
            let rightCI =  2 * index + 2;

            if (leftCI < this.values.length && ((this.values[leftCI][0] < this.values[swapIndex][0])
             || (this.values[leftCI][0] === this.values[swapIndex][0] && this.values[leftCI][1][1] < this.values[swapIndex][1][1]))) {
                swapIndex = leftCI;
            }

             if (rightCI < this.values.length && ((this.values[rightCI][0] < this.values[swapIndex][0])
             || (this.values[rightCI][0] === this.values[swapIndex][0] && this.values[rightCI][1][1] < this.values[swapIndex][1][1]))) {
                swapIndex = rightCI;
            }   

            if (swapIndex === index) {
                break;
            }

            this.swap(index, swapIndex);
            index = swapIndex;
        }
    }
}

